import React, { useContext, useState } from "react";
import "./CartItems.css";
import { ShopContext } from "../../Context/ShopContext";
import remove_icon from "../Assets/cart_cross_icon.png";
import axios from "axios";

const CartItems = () => {
  const {
    all_product,
    cartItems,
    removeFromCart,
    getTotalCartAmount,
  } = useContext(ShopContext);

  const [promoCode, setPromoCode] = useState("");
  const [discountPercent, setDiscountPercent] = useState(0);
  const [promoMessage, setPromoMessage] = useState("");

  const totalAmount = getTotalCartAmount();
  const discountAmount = (totalAmount * discountPercent) / 100;
  const finalAmount = totalAmount - discountAmount;

  // Handle promo code apply
  const applyPromoCode = async () => {
    if (!promoCode.trim()) {
      setPromoMessage("Please enter a promo code.");
      return;
    }
    try {
      const res = await axios.post("http://localhost:4001/validate-promo", {
        promoCode: promoCode.trim(),
      });
      if (res.data.success) {
        setDiscountPercent(res.data.discount);
        setPromoMessage(`Promo code applied! ${res.data.discount}% discount.`);
      } else {
        setDiscountPercent(0);
        setPromoMessage("Invalid promo code.");
      }
    } catch (err) {
      setDiscountPercent(0);
      setPromoMessage("Invalid promo code or server error.");
    }
  };

  // Handle proceed to checkout click
  const proceedToCheckout = () => {
    if (finalAmount <= 0) {
      alert("Cart is empty. Add some items first!");
      return;
    }
    alert(`Proceeding to checkout\n
Subtotal: ₹${totalAmount}\n
Discount: ₹${discountAmount}\n
Total to pay: ₹${finalAmount.toFixed(2)}
`);
    // You can replace this alert with actual checkout page redirect or payment integration
  };

  return (
    <div className="cartItems">
      <div className="cartItems-scroll">
        <div className="cartItems-format-main">
          <p>Products</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>
        <hr />
        {all_product.map((e) => {
          if (cartItems[e.id] > 0) {
            return (
              <div key={e.id}>
                <div className="cartItems-format cartItems-format-main cart-item-row">
                  <img
                    src={e.image}
                    alt=""
                    className="cartItem-product-icon"
                  />
                  <p>{e.name}</p>
                  <p>₹ {e.new_price}</p>
                  <button className="cartItem-quantity">
                    {cartItems[e.id]}
                  </button>
                  <p>₹ {e.new_price * cartItems[e.id]}</p>
                  <img
                    className="cartItem-remove-icon"
                    src={remove_icon}
                    alt="remove"
                    onClick={() => {
                      removeFromCart(e.id);
                    }}
                  />
                </div>
                <hr />
              </div>
            );
          }
          return null;
        })}
      </div>

      <div className="cartItems-down">
        <div className="cartItems-total">
          <h1>Cart Total</h1>
          <div>
            <div className="cartItems-total-item">
              <p>Subtotal</p>
              <p>₹{totalAmount}</p>
            </div>
            <hr />
            <div className="cartItems-total-item">
              <p>Discount</p>
              <p>₹{discountAmount.toFixed(2)}</p>
            </div>
            <hr />
            <div className="cartItems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartItems-total-item">
              <h3>Total</h3>
              <h3>₹{finalAmount.toFixed(2)}</h3>
            </div>
          </div>
          <button onClick={proceedToCheckout}>PROCEED TO CHECKOUT</button>
        </div>

        <div className="cartItems-promocode">
          <p>If you have a promo code, enter it here:</p>
          <div className="cartItems-promobox">
            <input
              type="text"
              placeholder="Promo code"
              value={promoCode}
              onChange={(e) => setPromoCode(e.target.value)}
            />
            <button onClick={applyPromoCode}>Apply</button>
          </div>
          {promoMessage && <p>{promoMessage}</p>}
        </div>
      </div>
    </div>
  );
};

export default CartItems;
