import React, { createContext, useEffect, useState } from "react";
import all_product from "../Components/Assets/all_product";
import axios from "axios";

export const ShopContext = createContext(null);

const ShopContextProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("auth-token"));

  const token = localStorage.getItem("auth-token");

  // Fetch cart from backend when logged in
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.post("http://localhost:4001/getcart", {}, {
          headers: { "auth-token": token },
        });
        setCartItems(res.data || {});
      } catch (err) {
        console.error("Error fetching cart:", err);
      }
    };

    if (token) {
      setIsLoggedIn(true);
      fetchCart();
    }
  }, [token]);

  const addToCart = async (itemId) => {
    if (!token) {
      alert("Please log in to add items to cart.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:4001/addtocart", { itemId }, {
        headers: { "auth-token": token },
      });
      setCartItems(res.data.cartData);
    } catch (err) {
      console.error("Error adding to cart:", err);
    }
  };

  const removeFromCart = async (itemId) => {
    if (!token) {
      alert("Please log in to remove items from cart.");
      return;
    }

    try {
      const res = await axios.post("http://localhost:4001/removefromcart", { itemId }, {
        headers: { "auth-token": token },
      });
      setCartItems(res.data.cartData);
    } catch (err) {
      console.error("Error removing from cart:", err);
    }
  };

  const getTotalCartAmount = () => {
    let total = 0;
    for (const item in cartItems) {
      const product = all_product.find(p => p.id === Number(item));
      if (product) {
        total += product.new_price * cartItems[item];
      }
    }
    return total;
  };

  const getTotalCartItems = () => {
    return Object.values(cartItems).reduce((a, b) => a + b, 0);
  };

  const logout = () => {
    localStorage.removeItem("auth-token");
    setIsLoggedIn(false);
    setCartItems({});
  };

  const contextValue = {
    all_product,
    cartItems,
    addToCart,
    removeFromCart,
    getTotalCartAmount,
    getTotalCartItems,
    isLoggedIn,
    logout,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
